  //                 className="btn btn-danger"
  //                 style={{ float: "right"}}
  //                 >
  //                 Delete
  //               </button>
  //             </div>
  //           </div>
  //         </div>
  //       </div>
  // ))

  return (
    <div className="container">
      <div>
        <h3>{displayDeck.name}</h3>
        <p>{displayDeck.description}</p>
      </div>
      <div>
        <button 
          type="button" 
          className="btn btn-secondary" 
          onClick
          >
          Edit
        </button>
        <button 
          type="button" 
          className="btn btn-primary" 
          style={{ marginLeft: "5px"}}
          onClick
          >
          Study
        </button>
        <button 
          type="button" 
          className="btn btn-primary" 
          style={{ marginLeft: "5px"}}
          onClick
          >
          Add Cards
        </button>
        <button 
          type="button" 
          className="btn btn-danger"
          style={{ float: "right" }}
          >
          Delete
        </button>
      </div>
      <br />
      <div>
        <h2>Cards</h2>
        {/* <section className="column">{cardList}</section> */}
      </div>
    </div>
  )
}

function Layout() {

  return (
    <Fragment>
      <Link to="/"></Link>
      <Link to="/decks/new"></Link>
      <Header />
      <div className="container">
        <Switch>
          <Route exact path="/">
            <DeckList />
          </Route>
          <Route exact path="/decks/new">
            <CreateDeck />
          </Route>
          <Route path={`/decks/:deckId`}>
            <DisplayDeck />
          </Route>
          <Route>
            <NotFound />
          </Route>
        </Switch>
      </div>
    </Fragment>
  );
}

export default Layout;
