import React, { Fragment, useEffect, useState } from "react";
import { Switch, Route, Link } from "react-router-dom"

import Header from "./Header";
import NotFound from "./NotFound";
import { listDecks, listCards } from "../utils/api"



function Deck({ deck }) {
  const [cardList, setCardList] = useState([]);

  useEffect(() => {
    async function runCardList() {
      const abortController = new AbortController();

      const cl = await listCards(deck.id, abortController.signal);
      setCardList(cl);

      return () => abortController.abort();
    };
    runCardList()
  }, [deck.id]);

  const noOfCards = cardList.filter(card => !card.cards).length

  return (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">{deck.name}</h5>
        <p>{noOfCards + " cards"}</p>
        <p className="card-text">{deck.description}</p>
        <button type="button" className="btn btn-secondary">View</button>
        <button type="button" className="btn btn-primary">Study</button>
        <button type="button" className="btn btn-danger">Delete Deck</button>
      </div>
    </div>
  );
}


function DeckList() {
    const [decks, setDecks] = useState([])

    useEffect(() => {
        const abortController = new AbortController()

        listDecks(abortController.signal).then(setDecks)

        return () => abortController.abort()
    }, [])

    const list = decks.map((deck) => <Deck key={deck.id} deck={deck} />)

    return (
        <main className="container">
            <section className="column">{list}</section>
        </main>
    )
}

function Layout() {
  return (
    <Fragment>
      <Header />
      <div className="container">
        <Link to="/"></Link>
        {/* TODO: Implement the screen starting here */}
        <Switch>
          <Route exact path="/">
            <button type="button" class="btn btn-secondary btn-lg">Create Deck</button>
            <DeckList />
          </Route>
          <Route>
            <NotFound />
          </Route>
        </Switch>
      </div>
    </Fragment>
  );
}

export default Layout;
