import React, { Fragment, useEffect, useState } from "react";
import { Switch, Route, Link, useHistory } from "react-router-dom";

import Header from "./Header";
import NotFound from "./NotFound";
import { listDecks } from "../utils/api";

function Deck({ deck }) {
  const [cardList, setCardList] = useState([]);

  useEffect(() => {
      listDecks().then(d => {
        const cards = d.find(d2 => d2.id === deck.id).cards
        setCardList(cards)
      })
  }, [deck.id])

  const noOfCards = cardList && cardList.filter((card) => !card.cards).length;

  return (
    <div className="card" style={{ margin: "5px" }}>
      <div className="card-body">
        <div style={{ justifyContent: "space-between" }}>
          <p className="text-muted" style={{ float: "right" }}>{noOfCards + " cards"}</p>
          <h5 className="card-title">{deck.name}</h5>
        </div>
        <p className="card-text">{deck.description}</p>
          <button 
            type="button" 
            className="btn btn-secondary" 
            onClick
            >
            View
          </button>
          <button 
            type="button" 
            className="btn btn-primary" 
            onClick
            style={{ marginLeft: "5px"}}
            >
            Study
          </button>
          <button 
            type="button" 
            className="btn btn-danger"
            style={{ float: "right" }}
            >
            Delete
          </button>
      </div>
    </div>
  );
}

function DeckList() {
  const [deckList, setDeckList] = useState([]);
  const history = useHistory()

  useEffect(() => {
    const abortController = new AbortController();

    listDecks(abortController.signal).then(setDeckList)

    return () => abortController.abort();
  }, []);

  const cardList =
    deckList.length > 0 ? (
      deckList.map((deck) => <Deck key={deck.id} deck={deck} />)
    ) : (
      <p />
    );

  return (
    <main className="container">
      <button 
        type="button" 
        className="btn btn-secondary btn-lg" 
        onClick={() => history.push('/decks/new')}
        style={{ margin: "5px" }}
        >
        Create Deck
      </button>
      <section className="column">{cardList}</section>
    </main>
  );
}

function CreateDeck() {
  const [name, setName] = useState("")
  const [description, setDescription] = useState("") 
  const handleNameChange = (event) => setName(event.target.value)
  const handleDescriptionChange = (event) => setDescription(event.target.value)
  const history = useHistory()

  return (
    <div className="container">
      <h1> Create Deck</h1>
    <form>
      <label htmlFor="name">Name</label> <br />
        <input
          id="name"
          type="text"
          name="name"
          placeholder="Deck Name"
          onChange={handleNameChange}
          style={{ width: "100%" }}
          value={name}
        /> <br />
        <br />
      <label htmlFor="description">Description</label><br />
        <textarea
        id="description"
        type="text"
        name="description"
        placeholder="Brief description of the deck"
        onChange={handleDescriptionChange}
        style={{ width: "100%", height: "150px" }}
        value={description}
        /> <br />
    </form>
    <div style={{ justifyContent: "flex-start"}}>
      <button type="submit" class="btn btn-primary" style={{ marginLeft: "5px" }}>Submit</button>
      <button type="button" class="btn btn-secondary" style={{ marginLeft : "5px" }} onClick={() => history.push(-1)}>Cancel</button>
    </div>
    </div>
  );
}

// function handleClick(arg) {

//   if (arg === "Create Deck") {
//     return <Link to={"/decks/new"}></Link>

//   }

//   if (arg === "view") {

//   }

//   if (arg === "study") {

//   }

// }

function Layout() {

  return (
    <Fragment>
      <Link to="/"></Link>
      <Link to="/decks/new"></Link>
      <Header />
      <div className="container">
        <Switch>
          <Route exact path="/">
            <DeckList />
          </Route>
          <Route exact path="/decks/new">
            <CreateDeck />
          </Route>
          <Route>
            <NotFound />
          </Route>
        </Switch>
      </div>
    </Fragment>
  );
}

export default Layout;
