import React, { Fragment, useEffect, useState } from "react";
import { Switch, Route, Link } from "react-router-dom";

import Header from "./Header";
import NotFound from "./NotFound";
import { listDecks } from "../utils/api";

function Deck({ deck }) {
  const [cardList, setCardList] = useState([]);

  useEffect(() => {
      listDecks().then(d => {
        const cards = d.find(d2 => d2.id === deck.id).cards
        setCardList(cards)
      })
  }, [deck.id])

  const noOfCards = cardList && cardList.filter((card) => !card.cards).length;

  return (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">{deck.name}</h5>
        <p>{noOfCards + " cards"}</p>
        <p className="card-text">{deck.description}</p>
        <button type="button" className="btn btn-secondary" onClick={handleClick("view")}>
          View
        </button>
        <button type="button" className="btn btn-primary" onClick={handleClick("study")}>
          Study
        </button>
        <button type="button" className="btn btn-danger">
          Delete Deck
        </button>
      </div>
    </div>
  );
}

function DeckList() {
  const [deckList, setDeckList] = useState([]);

  useEffect(() => {
    const abortController = new AbortController();

    listDecks(abortController.signal).then(setDeckList)

    return () => abortController.abort();
  }, []);

  const cardList =
    deckList.length > 0 ? (
      deckList.map((deck) => <Deck key={deck.id} deck={deck} />)
    ) : (
      <p />
    );

  return (
    <main className="container">
      <section className="column">{cardList}</section>
    </main>
  );
}

function CreateDeck() {
  const [name, setName] = useState("")
  const handleNameChange = (event) => setName(event.target.value)
  return (
    <form>
      <label htmlFor="name">
        Enter Your Name:
        <input
          id="name"
          type="text"
          name="name"
          onChange={handleNameChange}
          value={name}
        />
      </label>
    </form>
  );
}

function handleClick(arg) {

  if (arg === "Create Deck") {
    return <Link to={"/decks/new"}></Link>

  }

  if (arg === "view") {

  }

  if (arg === "study") {

  }

}

function Layout() {
  return (
    <Fragment>
      <Link to="/"></Link>
      <Link to="/decks/new"></Link>
      <Header />
      <div className="container">
        <Switch>
          <Route exact path="/">
            <button type="button" className="btn btn-secondary btn-lg" onClick={handleClick("Create Deck")}>
              Create Deck
            </button>
            <DeckList />
          </Route>
          <Route exact path="/decks/new">
            <CreateDeck />
          </Route>
          <Route>
            <NotFound />
          </Route>
        </Switch>
      </div>
    </Fragment>
  );
}

export default Layout;
