import React, { Fragment, useEffect, useState } from "react";
import { Switch, Route, Link, useHistory, useParams } from "react-router-dom";

import Header from "./Header";
import NotFound from "./NotFound";
import DeckList from "./DeckList";
import CreateDeck from "./CreateDeck";
import DisplayDeck from "./DisplayDeck";
import StudyDeck from "./StudyDeck"
import AddCard from "./AddCard"
import EditDeck from "./EditDeck"
import { listDecks, readDeck, listCards, readCard } from "../utils/api";


function EditCard() {
  const [selectedDeck, setSelectedDeck] = useState([])
  const [selectedCard, setSelectedCard] = useState([])
  const { deckId, cardId } = useParams()
  const [front, setFront] = useState("")
  const [back, setBack] = useState("")
  const handleFrontChange = (event) => setFront(event.target.value)
  const handleBackChange = (event) => setBack(event.target.value)

  useEffect(() => {
    const abortController = new AbortController()
    listDecks(deckId ,abortController.signal).then(decks => {
      return decks.find(deck => deck.id === parseInt(deckId))
    })
    .then(deck => {
      setSelectedDeck(deck)
      readCard(cardId, abortController.signal).then(setSelectedCard)
    })
  }, [deckId])

  console.log(selectedDeck)

  // const cardList = selectedDeck && selectedDeck.cards
  // const selectedCard = cardList && cardList.find(card => card.id === parseInt(cardId))

  return (
    <div className="container">
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
          <li className="breadcrumb-item"><Link to="/">Home</Link></li>
          <li className="breadcrumb-item active" aria-current="page"><Link to={`/decks/${selectedDeck.id}`}>{selectedDeck.name}</Link></li>
          <li className="breadcrumb-item active" aria-current="page">{"Edit Card " + cardId}</li>
        </ol>
      </nav>
      <h3>Edit Card</h3>
      <form>
        <label htmlFor="name">Front</label> <br />
          <textarea
            id="name"
            type="text"
            name="name"
            placeholder={`${selectedCard.front}`}
            onChange={handleFrontChange}
            style={{ width: "100%", height: "150px" }}
            value={front}
          /> <br />
          <br />
        <label htmlFor="description">Back</label><br />
          <textarea
          id="description"
          type="text"
          name="description"
          placeholder={`${selectedCard.back}`}
          onChange={handleBackChange}
          style={{ width: "100%", height: "150px" }}
          value={back}
          /> <br />
      </form>
      <div style={{ justifyContent: "flex-start"}}>
        <button type="submit" class="btn btn-secondary" style={{ marginLeft: "5px" }}>Done</button>
        <Link to={`/decks/${selectedDeck.id}`}>
          <button type="button" class="btn btn-primary" style={{ marginLeft : "5px" }}>Save</button>
        </Link>
      </div>
    </div>
  );
}

function Layout() {
  return (
    <Fragment>
      <Link to="/"></Link>
      <Link to="/decks/new"></Link>
      <Header />
      <div className="container">
        <Switch>
          <Route exact path="/">
            <DeckList />
          </Route>
          <Route exact path="/decks/new">
            <CreateDeck />
          </Route>
          <Route exact path={`/decks/:deckId`}>
            <DisplayDeck />
          </Route>
          <Route exact path={`/decks/:deckId/study`}>
            <StudyDeck />
          </Route>
          <Route exact path={`/decks/:deckId/cards/new`}>
            <AddCard />
          </Route>
          <Route exact path={`/decks/:deckId/edit`}>
            <EditDeck />
          </Route>
          <Route exact path={`/decks/:deckId/cards/:cardId`}>
            <EditCard />
          </Route>
          <Route>
            <NotFound />
          </Route>
        </Switch>
      </div>
    </Fragment>
  );
}

export default Layout;
