import React, { Fragment, useEffect, useState } from "react";
import { Switch, Route, Link, useHistory, useParams } from "react-router-dom";

import Header from "./Header";
import NotFound from "./NotFound";
import DeckList from "./DeckList"
import CreateDeck from "./CreateDeck"
import DisplayDeck from "./DisplayDeck"
import { listDecks, readDeck, listCards } from "../utils/api";

function StudyDeck() {
  const [selectedDeck, setSelectedDeck] = useState([])
  const [cardList, setCardList] = useState([])
  // const [selectedCard, setSelectedCard] = useState([])
  const { deckId } = useParams()

  useEffect(() => {
    const abortController = new AbortController()
    listDecks().then(decks => {
      return decks.find(d => d.id === parseInt(deckId))
    })
    .then(deck => {
      setSelectedDeck(deck)
      listCards(deck.id, abortController.signal).then(setCardList)
    })
    return () => abortController.abort()
  }, [deckId])


  const validCards = cardList && cardList.filter(card => !card.cards)
  console.log(validCards)

  return (
    <div className="container">
      <h1> {"Study: " + selectedDeck.name}</h1>
      <div class="card">
        <div class="card-body">
          <h5 class="card-title">{" of " + validCards.length}</h5>
          <p class="card-text">With supporting text below as a natural lead-in to additional content.</p>
          <button 
            type="button" 
            className="btn btn-primary" 
            style={{ float: "left"}}
            onClick
            >
            flip
          </button>
        </div>
      </div>
    </div>
  )
}

function Layout() {

  return (
    <Fragment>
      <Link to="/"></Link>
      <Link to="/decks/new"></Link>
      <Header />
      <div className="container">
        <Switch>
          <Route exact path="/">
            <DeckList />
          </Route>
          <Route exact path="/decks/new">
            <CreateDeck />
          </Route>
          <Route exact path={`/decks/:deckId`}>
            <DisplayDeck />
          </Route>
          <Route exact path={`/decks/:deckId/study`}>
            <StudyDeck />
          </Route>
          <Route>
            <NotFound />
          </Route>
        </Switch>
      </div>
    </Fragment>
  );
}

export default Layout;
