import React, { Fragment, useEffect, useState } from "react";
import { Switch, Route, Link } from "react-router-dom"

import Header from "./Header";
import NotFound from "./NotFound";
import { listCards, listDecks } from "../utils/api";

function ErrorMessage ({ error }) {
  return (
    <main className="container">
      <p style={{ color: "red" }}>ERROR: {error.message}</p>
    </main>
  )
}

function Deck({ deck }) {
  const [cardList, setCardList] = useState([]);
  const [error, setError] = useState(undefined)

  console.log(deck.id)
  useEffect(() => {
      listCards(deck.id).then(i => setCardList(i)).catch(setError)
      // setCardList(listCards(deck.i))
  }, [deck.id])

  if (error) {
    return <ErrorMessage error={error} />
  }

  const noOfCards = cardList.filter(card => !card.cards).length

  return (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">{deck.name}</h5>
        <p>{noOfCards + " cards"}</p>
        <p className="card-text">{deck.description}</p>
        <button type="button" className="btn btn-secondary">View</button>
        <button type="button" className="btn btn-primary">Study</button>
        <button type="button" className="btn btn-danger">Delete Deck</button>
      </div>
    </div>
  );
}


function DeckList() {
    const [deckList, setDeckList] = useState([])
    const [error, setError] = useState(undefined)

    useEffect(() => {
        const abortController = new AbortController()

        listDecks(abortController.signal).then(setDeckList).catch(setError)

        return () => abortController.abort()
    }, [])

  if (error) {
    return <ErrorMessage error={error} />
  }
    const cardList = deckList.length > 0 ? deckList.map((deck) => <Deck key={deck.id} deck={deck} />) : <p />

    return (
        <main className="container">
            <section className="column">{cardList}</section>
        </main>
    )
}

function Layout() {
  return (
    <Fragment>
      <Link to="/"></Link>
      <Header />
      <div className="container">
        <Switch>
          <Route exact path="/">
            <button type="button" className="btn btn-secondary btn-lg">Create Deck</button>
            <DeckList />
          </Route>
          <Route>
            <NotFound />
          </Route>
        </Switch>
      </div>
    </Fragment>
  );
}

export default Layout;
