import React, { Fragment, useEffect, useState } from "react";
import { Switch, Route, Link, useHistory, useParams } from "react-router-dom";

import Header from "./Header";
import NotFound from "./NotFound";
import DeckList from "./DeckList"
import CreateDeck from "./CreateDeck"
import DisplayDeck from "./DisplayDeck"
import { listDecks, readDeck, listCards } from "../utils/api";

function StudyDeck() {
  const [selectedDeck, setSelectedDeck] = useState([])
  // const [selectedCard, setSelectedCard] = useState([])
  const { deckId } = useParams()

  useEffect(() => {
    const abortController = new AbortController()
    listDecks().then(decks => {
      return decks.find(d => d.id === parseInt(deckId))
    })
    .then(deck => {
      listCards(deck.id, abortController.signal).then(setSelectedDeck)
    })
    return () => abortController.abort()
  }, [deckId])

  console.log(selectedDeck)
  // const cardList = selectedDeck && selectedDeck.cardList

  return <p>{selectedDeck}</p>
}

function Layout() {

  return (
    <Fragment>
      <Link to="/"></Link>
      <Link to="/decks/new"></Link>
      <Header />
      <div className="container">
        <Switch>
          <Route exact path="/">
            <DeckList />
          </Route>
          <Route exact path="/decks/new">
            <CreateDeck />
          </Route>
          <Route exact path={`/decks/:deckId`}>
            <DisplayDeck />
          </Route>
          <Route exact path={`/decks/:deckId/study`}>
            <StudyDeck />
          </Route>
          <Route>
            <NotFound />
          </Route>
        </Switch>
      </div>
    </Fragment>
  );
}

export default Layout;
