import React, { Fragment, useState } from "react"
import { useHistory } from "react-router-dom"

export default function StudyCardLogic(props) {
  const { validCards } = props
  const history = useHistory()
  const [flipped, setFlipped] = useState(false);
  const [selectedCard, setSelectedCard] = useState(0);

    return (
        <div className="card">
          <div className="card-body">
            <h5 className="card-title">
              {selectedCard + 1 + " of " + validCards.length}
            </h5>
            <Fragment>
              {!flipped ? (
            <Fragment>
              <p className="card-text">{validCards[selectedCard].front}</p>
              <button
                type="button"
                className="btn btn-secondary"
                style={{ float: "left" }}
                onClick={() => setFlipped(true)}
              >
                Flip
              </button>
            </Fragment>
            ) : (
              <Fragment>
                <p className="card-text">{validCards[selectedCard].back}</p>
                <button
                  type="button"
                  className="btn btn-secondary"
                  style={{ float: "left" }}
                  onClick={() => setFlipped(false)}
                >
                  Flip
                </button>
                <button
                  type="button"
                  className="btn btn-primary"
                  style={{ float: "left", marginLeft: "5px" }}
                  onClick={() => {
                    const nextCard = selectedCard + 1
                    if(validCards[nextCard]) {
                    setSelectedCard(nextCard)
                    setFlipped(false)
                    }
                    else if (window.confirm("Restart Cards?")) {
                      setSelectedCard(0)
                      setFlipped(false)
                    }
                    else {
                      history.push("/")
                    }
                  }}
                >
                  Next
                </button>
              </Fragment>
              )}
            </Fragment>
          </div>
      </div>
    )
}
