import React, { Fragment, useEffect, useState } from "react";
import { Switch, Route, Link, useHistory, useParams } from "react-router-dom";

import Header from "./Header";
import NotFound from "./NotFound";
import DeckList from "./DeckList";
import CreateDeck from "./CreateDeck";
import DisplayDeck from "./DisplayDeck";
import { listDecks, readDeck, listCards } from "../utils/api";

function StudyDeck() {
  const [selectedDeck, setSelectedDeck] = useState([]);
  const [cardList, setCardList] = useState([]);
  const [flipped, setFlipped] = useState(false);
  const [selectedCard, setSelectedCard] = useState(0);
  const { deckId } = useParams();
  const history = useHistory()

  useEffect(() => {
    const abortController = new AbortController();
    listDecks()
      .then((decks) => {
        return decks.find((d) => d.id === parseInt(deckId));
      })
      .then((deck) => {
        setSelectedDeck(deck);
        listCards(deck.id, abortController.signal).then(setCardList);
      });
    return () => abortController.abort();
  }, [deckId]);

  if (cardList.length === 0) return <></>;
  const validCards = cardList.filter((card) => !card.cards);
  // console.log(validCards[1]);

  return (
    <div className="container">
      <nav aria-label="breadcrumb">
        <ol class="breadcrumb">
          <li class="breadcrumb-item"><Link to="/">Home</Link></li>
          <li class="breadcrumb-item active" aria-current="page"><Link to={`/decks/${selectedDeck.id}`}>{selectedDeck.name}</Link></li>
          <li class="breadcrumb-item active" aria-current="page">Study</li>
        </ol>
      </nav>
      <h1> {"Study: " + selectedDeck.name}</h1>
      <div class="card">
        <div class="card-body">
          <h5 class="card-title">
            {selectedCard + 1 + " of " + validCards.length}
          </h5>
          {!flipped ? (
            <Fragment>
              <p class="card-text">{validCards[selectedCard].front}</p>
              <button
                type="button"
                className="btn btn-secondary"
                style={{ float: "left" }}
                onClick={() => setFlipped(true)}
              >
                Flip
              </button>
            </Fragment>
          ) : (
            <Fragment>
              <p class="card-text">{validCards[selectedCard].back}</p>
              <button
                type="button"
                className="btn btn-secondary"
                style={{ float: "left" }}
                onClick={() => setFlipped(false)}
              >
                Flip
              </button>
              <button
                type="button"
                className="btn btn-primary"
                style={{ float: "left", marginLeft: "5px" }}
                onClick={() => {
                  const nextCard = selectedCard + 1
                  if(validCards[nextCard]) {
                  setSelectedCard(nextCard)
                  setFlipped(false)
                  }
                  else if (window.confirm("Restart Cards?")) {
                    setSelectedCard(0)
                    setFlipped(false)
                  }
                  else {
                    history.push("/")
                  }
                }}
              >
                Next
              </button>
            </Fragment>
          )}
        </div>
      </div>
    </div>
  );
}

function Layout() {
  return (
    <Fragment>
      <Link to="/"></Link>
      <Link to="/decks/new"></Link>
      <Header />
      <div className="container">
        <Switch>
          <Route exact path="/">
            <DeckList />
          </Route>
          <Route exact path="/decks/new">
            <CreateDeck />
          </Route>
          <Route exact path={`/decks/:deckId`}>
            <DisplayDeck />
          </Route>
          <Route exact path={`/decks/:deckId/study`}>
            <StudyDeck />
          </Route>
          <Route>
            <NotFound />
          </Route>
        </Switch>
      </div>
    </Fragment>
  );
}

export default Layout;
