import React, { Fragment, useEffect, useState } from "react";
import { Switch, Route, Link, useHistory, useParams } from "react-router-dom";

import Header from "./Header";
import NotFound from "./NotFound";
import DeckList from "./DeckList";
import CreateDeck from "./CreateDeck";
import DisplayDeck from "./DisplayDeck";
import StudyDeck from "./StudyDeck"
import { listDecks, readDeck, listCards } from "../utils/api";

function AddCard() {
  const [selectedDeck, setSelectedDeck] = useState([])
  const { deckId } = useParams()
  const [front, setFront] = useState("")
  const [back, setBack] = useState("")
  const handleFrontChange = (event) => setFront(event.target.value)
  const handleBackChange = (event) => setBack(event.target.value)

  useEffect(() => {
    const abortController = new AbortController();
    listDecks(abortController.signal)
      .then((decks) => {
        return decks.find((d) => d.id === parseInt(deckId));
      })
      .then(setSelectedDeck)

    return () => abortController.abort();
  }, [deckId]);

    return (
    <div className="container">
      <nav aria-label="breadcrumb">
        <ol class="breadcrumb">
          <li class="breadcrumb-item"><Link to="/">Home</Link></li>
          <li class="breadcrumb-item active" aria-current="page"><Link to={`/decks/${selectedDeck.id}`}>{selectedDeck.name}</Link></li>
          <li class="breadcrumb-item active" aria-current="page">Study</li>
        </ol>
      </nav>
      <h1> Create Deck</h1>
      <form>
        <label htmlFor="name">Name</label> <br />
          <textarea
            id="name"
            type="text"
            name="name"
            placeholder="Front side of card"
            onChange={handleFrontChange}
            style={{ width: "100%", height: "150px" }}
            value={front}
          /> <br />
          <br />
        <label htmlFor="description">Description</label><br />
          <textarea
          id="description"
          type="text"
          name="description"
          placeholder="Back side of card"
          onChange={handleBackChange}
          style={{ width: "100%", height: "150px" }}
          value={back}
          /> <br />
      </form>
      <div style={{ justifyContent: "flex-start"}}>
        <button type="submit" class="btn btn-primary" style={{ marginLeft: "5px" }}>Submit</button>
        <button type="button" class="btn btn-secondary" style={{ marginLeft : "5px" }} onClick={() => history.push("/")}>Cancel</button>
      </div>
    </div>
  );
}


function Layout() {
  return (
    <Fragment>
      <Link to="/"></Link>
      <Link to="/decks/new"></Link>
      <Header />
      <div className="container">
        <Switch>
          <Route exact path="/">
            <DeckList />
          </Route>
          <Route exact path="/decks/new">
            <CreateDeck />
          </Route>
          <Route exact path={`/decks/:deckId`}>
            <DisplayDeck />
          </Route>
          <Route exact path={`/decks/:deckId/study`}>
            <StudyDeck />
          </Route>
          <Route exact path={`/decks/:deckId/cards/new`}>
            <AddCard />
          </Route>
          <Route>
            <NotFound />
          </Route>
        </Switch>
      </div>
    </Fragment>
  );
}

export default Layout;
