import React, { Fragment, useEffect, useState } from "react";
import { Switch, Route, Link, useHistory, useParams } from "react-router-dom";

import Header from "./Header";
import NotFound from "./NotFound";
import DeckList from "./DeckList"
import CreateDeck from "./CreateDeck"
import { readDeck } from "../utils/api";


function DisplayDeck({ deck }) {
  console.log(deck)
  // const [deck, setDeck] = useState([])

  // useEffect(() => {
  //   const abortController = new AbortController()

  //   readDeck(deck.id, abortController.signal).then(setDeck)

  //   return () => abortController.abort()
  // }, [deck.id])

  return (
    <div className="container">
      <div>
        <h3>{deck.name}</h3>
        <p>{deck.description}</p>
      </div>
      <div>
        <button 
          type="button" 
          className="btn btn-secondary" 
          onClick
          >
          Edit
        </button>
        <button 
          type="button" 
          className="btn btn-primary" 
          style={{ marginLeft: "5px"}}
          onClick
          >
          Study
        </button>
        <button 
          type="button" 
          className="btn btn-primary" 
          style={{ marginLeft: "5px"}}
          onClick
          >
          Add Cards
        </button>
        <button 
          type="button" 
          className="btn btn-danger"
          style={{ float: "right" }}
          >
          Delete
        </button>
      </div>
      <div>
        <h2>Cards</h2>
        {/* <section className="column">{cardList}</section> */}
      </div>
    </div>
  )
}

function Layout() {

  return (
    <Fragment>
      <Link to="/"></Link>
      <Link to="/decks/new"></Link>
      <Header />
      <div className="container">
        <Switch>
          <Route exact path="/">
            <DeckList />
          </Route>
          <Route exact path="/decks/new">
            <CreateDeck />
          </Route>
          <Route path={`/decks/1`}>
            <DisplayDeck deck={readDeck(1)}/>
          </Route>
          <Route>
            <NotFound />
          </Route>
        </Switch>
      </div>
    </Fragment>
  );
}

export default Layout;
